import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

interface State {
  isRecording: boolean
  mediaRecorder: MediaRecorder | null
  audioBlob: Blob | null
  chunks: Blob[]
}

class StreamlitAudioRecorder extends StreamlitComponentBase {
  constructor(props: any) {
    super(props)
    this.state = {
      isRecording: false,
      mediaRecorder: null,
      audioBlob: null,
      chunks: []
    }
  }

  startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: {
          channelCount: 1,
          sampleRate: 44100,
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      })

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm;codecs=opus',
        audioBitsPerSecond: 128000
      })

      mediaRecorder.ondataavailable = (e: BlobEvent) => {
        if (e.data.size > 0) {
          this.setState((prev: State) => ({
            chunks: [...prev.chunks, e.data]
          }))
        }
      }

      mediaRecorder.onstop = () => {
        const blob = new Blob(this.state.chunks, { type: 'audio/webm;codecs=opus' })

        // First update UI state
        this.setState({ 
          chunks: [], 
          audioBlob: blob,
          isRecording: false,
          mediaRecorder: null
        })

        // Then convert and send data
        const reader = new FileReader()
        reader.onloadend = () => {
          const base64Audio = (reader.result as string).split(',')[1]
          Streamlit.setComponentValue({ arr: Object.fromEntries([...new Uint8Array(blob.size).entries()]) })
        }
        reader.readAsDataURL(blob)
      }

      mediaRecorder.start(100)
      this.setState({ mediaRecorder, isRecording: true })

    } catch (err) {
      console.error("Error accessing microphone:", err)
    }
  }

  stopRecording = () => {
    if (this.state.mediaRecorder && this.state.isRecording) {
      this.state.mediaRecorder.stop()
      this.state.mediaRecorder.stream.getTracks().forEach((track: MediaStreamTrack) => track.stop())
    }
  }

  resetRecording = (): void => {
    this.setState({
      isRecording: false,
      audioBlob: null,
      chunks: []
    })
    Streamlit.setComponentValue(null)
  }

  downloadRecording = (): void => {
    if (this.state.audioBlob) {
      const datetime = new Date().toLocaleString()
        .replace(/[\s,]/g, '')
        .replace(/_/g, '')
      const filename = `streamlit_audio_${datetime}.webm`

      const a = document.createElement('a')
      a.style.display = 'none'
      a.href = URL.createObjectURL(this.state.audioBlob)
      a.download = filename
      document.body.appendChild(a)
      a.click()
      document.body.removeChild(a)
    }
  }

  public render = (): ReactNode => {
    const theme = this.props.theme
    const style: React.CSSProperties = {}

    if (theme) {
      const borderStyling = `1px solid ${
        this.state.isRecording ? theme.primaryColor : "gray"
      }`
      style.border = borderStyling
      style.outline = borderStyling
    }

    return (
      <div>
        <button 
          id="record" 
          onClick={this.state.isRecording ? this.stopRecording : this.startRecording}
          style={style}
        >
          {this.state.isRecording ? 'Stop Recording' : 'Start Recording'}
        </button>
        <button id="reset" onClick={this.resetRecording} style={style}>
          Reset
        </button>
        <button id="download" onClick={this.downloadRecording} style={style}>
          Download
        </button>
        {this.state.audioBlob && (
          <audio
            id="audio"
            controls
            src={URL.createObjectURL(this.state.audioBlob)}
          />
        )}
      </div>
    )
  }
}

export default withStreamlitConnection(StreamlitAudioRecorder)

Streamlit.setComponentReady()
Streamlit.setFrameHeight()
